CREATE DATABASE IF NOT EXISTS `kampung_naga`;

USE `kampung_naga`;

CREATE TABLE IF NOT EXISTS `accounts` (
	`id` INTEGER UNSIGNED AUTO_INCREMENT,
	`email` VARCHAR(255),
	`username` VARCHAR(128),
	`password` VARCHAR(255),
	`first_name` VARCHAR(128),
	`last_name` VARCHAR(128),
	`birth_date` DATE,
	`gender` BIT DEFAULT 0,
	`profile_picture` VARCHAR(255) DEFAULT 'default.jpg',
	`created_at` DATETIME,
	`updated_at` DATETIME,
	`is_admin` BIT DEFAULT 0,
	PRIMARY KEY (`id`),
	UNIQUE (`email`),
	UNIQUE (`username`)
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `categories` (
	`category` VARCHAR(255),
	`category_slug` VARCHAR(255),
	`created_at` DATETIME DEFAULT CURRENT_TIMESTAMP,
	PRIMARY KEY (`category`)
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `articles` (
	`id` INTEGER UNSIGNED AUTO_INCREMENT,
	`title` VARCHAR(255),
	`title_slug` VARCHAR(255),
	`category` VARCHAR(255),
	`category_slug` VARCHAR(255),
	`thumbnail` VARCHAR(255),
	`author` VARCHAR(255),
	`is_trending` BIT DEFAULT 0,
	`content` LONGTEXT,
	`created_at` DATETIME,
	`updated_at` DATETIME,
	PRIMARY KEY (`id`),
	UNIQUE (`title`),
	UNIQUE (`title_slug`),
	FOREIGN KEY (`category`) REFERENCES `categories`(`category`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `article_likes` (
	`account_id` INTEGER UNSIGNED,
	`article_id` INTEGER UNSIGNED,
	CONSTRAINT `pk_article_like` PRIMARY KEY (`account_id`, `article_id`),
	FOREIGN KEY (`account_id`) REFERENCES `accounts`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
	FOREIGN KEY (`article_id`) REFERENCES `articles`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `article_comments` (
	`id` INTEGER UNSIGNED AUTO_INCREMENT,
	`account_id` INTEGER UNSIGNED,
	`article_id` INTEGER UNSIGNED,
	`comment_text` MEDIUMTEXT,
	`created_at` DATETIME,
	`updated_at` DATETIME,
	PRIMARY KEY (`id`),
	CONSTRAINT `pk_article_comment` UNIQUE (`account_id`, `article_id`),
	FOREIGN KEY (`account_id`) REFERENCES `accounts`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
	FOREIGN KEY (`article_id`) REFERENCES `articles`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `article_comment_likes` (
	`account_id` INTEGER UNSIGNED,
	`comment_id` INTEGER UNSIGNED,
	CONSTRAINT `pk_comment_like` PRIMARY KEY (`account_id`, `comment_id`),
	FOREIGN KEY (`account_id`) REFERENCES `accounts`(`id`) ON UPDATE CASCADE ON DELETE CASCADE,
	FOREIGN KEY (`comment_id`) REFERENCES `article_comments`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `admin_log` (
	`id` INTEGER UNSIGNED AUTO_INCREMENT,
	`datetime` DATETIME DEFAULT CURRENT_TIMESTAMP,
	`admin_id` INTEGER UNSIGNED,
	`admin_name` VARCHAR(255),
	`action` VARCHAR(255),
	PRIMARY KEY (`id`),
	FOREIGN KEY (`admin_id`) REFERENCES `accounts`(`id`) ON UPDATE CASCADE ON DELETE CASCADE
) ENGINE=InnoDB;

